mapper.insert:
INSERT INTO user (id
    , password
    , user_name
    , authority
    , phone_number
    , email
    , corp_id)
VALUES (#{id}
    , #{password}
    , #{userName}
    , #{authority}
    , #{phoneNumber}
    , #{email}
    , #{corpId})

mapper.update:
    	UPDATE user
		SET email=#{email}, phone_number=#{phoneNumber}
		WHERE id=#{id};

    	UPDATE user
		SET password=#{password}
		WHERE id=#{id};
